// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract QuizPlatform {
    struct Question {
        string questionText;
        string questionImg;
        string[] options;
        uint correctOption;
    }

    struct Reward {
        string label;
        uint value;
    }

    struct Quiz {
        string title;
        string description;
        string imageUrl;
        uint entranceFee;
        uint pricePool;
        uint timer;
        Question[] questions;
        Reward[] rewards;
        address organizer;
        bool isActive;
        bool isEnded;
        string visibility;
    }

    struct Participant {
        uint numQuestionsPassed;
        uint numQuestionsFailed;
        uint points;
        uint grade;
        uint attempts;
    }

    Quiz[] public quizzes;
    mapping(uint => mapping(address => Participant)) public quizParticipants;
    mapping(uint => address[]) public quizParticipantAddresses; // Updated mapping to store unique participant addresses

    event QuizCreated(uint indexed quizId, address indexed organizer, string title, string description, uint entranceFee, uint pricePool, uint timer, string visibility);
    event ParticipantAdded(uint indexed quizId, address indexed participant);
    event QuizClosed(uint indexed quizId);
    event RewardsDistributed(uint indexed quizId, address[] participants, uint[] amounts);

    function createQuiz(
        string memory title,
        string memory description,
        string memory imageUrl,
        uint entranceFee,
        uint pricePool,
        uint timer,
        Question[] memory questions,
        Reward[3] memory rewards,
        string memory visibility
    ) public payable {
        require(msg.value == pricePool, "Price pool must be paid in TFUEL");

        Quiz storage newQuiz = quizzes.push();
        newQuiz.title = title;
        newQuiz.description = description;
        newQuiz.imageUrl = imageUrl;
        newQuiz.entranceFee = entranceFee;
        newQuiz.pricePool = pricePool;
        newQuiz.timer = timer;
        newQuiz.organizer = msg.sender;
        newQuiz.isActive = true;
        newQuiz.isEnded = false;
        newQuiz.visibility = visibility;

        for (uint i = 0; i < questions.length; i++) {
            newQuiz.questions.push(Question({
                questionText: questions[i].questionText,
                questionImg: questions[i].questionImg,
                options: questions[i].options,
                correctOption: questions[i].correctOption
            }));
        }

        for (uint j = 0; j < rewards.length; j++) {
            newQuiz.rewards.push(rewards[j]);
        }

        emit QuizCreated(quizzes.length - 1, msg.sender, title, description, entranceFee, pricePool, timer, visibility);
    }

    function participateInQuiz(uint quizId) public payable {
        Quiz storage quiz = quizzes[quizId];
        require(msg.value == quiz.entranceFee, "Incorrect entrance fee in TFUEL");
        require(quiz.isActive, "Quiz is not active");

        Participant storage participant = quizParticipants[quizId][msg.sender];
        if (participant.attempts == 0) {
            quizParticipantAddresses[quizId].push(msg.sender); // Add participant address only if it's the first attempt
        }
        participant.attempts += 1;
        emit ParticipantAdded(quizId, msg.sender);
    }

    function submitQuizResult(uint quizId, uint grade, uint numQuestionsFailed, uint numQuestionsPassed, uint points) public {
        Quiz storage quiz = quizzes[quizId];
        require(quizParticipants[quizId][msg.sender].attempts > 0, "Not a participant");
        require(quiz.isActive, "Quiz is not active");

        Participant storage participant = quizParticipants[quizId][msg.sender];
        participant.grade = grade;
        participant.numQuestionsFailed = numQuestionsFailed;
        participant.numQuestionsPassed = numQuestionsPassed;
        participant.points = points;

        // Remove automatic reward calculation and transfer
    }

    function distributeRewards(uint quizId, address[] memory participants, uint[] memory amounts) public {
        Quiz storage quiz = quizzes[quizId];
        require(msg.sender == quiz.organizer, "Only the organizer can distribute rewards");
        require(quiz.isActive, "Quiz is not active");
        require(participants.length == amounts.length, "Participants and amounts length mismatch");

        for (uint i = 0; i < participants.length; i++) {
            require(quiz.pricePool >= amounts[i], "Not enough funds in the prize pool");
            quiz.pricePool -= amounts[i];
            payable(participants[i]).transfer(amounts[i]);
        }

        quiz.isActive = false;
        quiz.isEnded = true;
        emit RewardsDistributed(quizId, participants, amounts);
        emit QuizClosed(quizId);
    }

    function getQuiz(uint quizId) public view returns (
    string memory title,
    string memory description,
    string memory imageUrl,
    uint entranceFee,
    uint pricePool,
    uint timer,
    bool isActive,
    bool isEnded,
    Reward[] memory rewards
) {
    Quiz storage quiz = quizzes[quizId];
    return (
        quiz.title,
        quiz.description,
        quiz.imageUrl,
        quiz.entranceFee,
        quiz.pricePool,
        quiz.timer,
        quiz.isActive,
        quiz.isEnded,
        quiz.rewards
    );
}

    function getQuizQuestions(uint quizId) public view returns (Question[] memory) {
        Quiz storage quiz = quizzes[quizId];
        return quiz.questions;
    }

    function getQuizzesCount() public view returns (uint) {
        return quizzes.length;
    }

    function isParticipant(uint quizId, address participant) public view returns (bool) {
        return quizParticipants[quizId][participant].attempts > 0;
    }

    function getParticipantDetails(uint quizId, address participant) public view returns (
        uint numQuestionsPassed,
        uint numQuestionsFailed,
        uint points,
        uint grade
    ) {
        Participant storage p = quizParticipants[quizId][participant];
        return (p.numQuestionsPassed, p.numQuestionsFailed, p.points, p.grade);
    }

    function getQuizParticipants(uint quizId) public view returns (address[] memory) {
        return quizParticipantAddresses[quizId];
    }

    function getQuizParticipantsCount(uint quizId) public view returns (uint) {
        return quizParticipantAddresses[quizId].length;
    }

    function getQuizParticipant(uint quizId, uint index) public view returns (address) {
        require(index < quizParticipantAddresses[quizId].length, "Index out of bounds");
        return quizParticipantAddresses[quizId][index];
    }

    function getEndedQuizzesByParticipant(address participant) public view returns (Quiz[] memory) {
        uint count = 0;
        for (uint i = 0; i < quizzes.length; i++) {
            if (quizzes[i].isEnded && quizParticipants[i][participant].attempts > 0) {
                count++;
            }
        }

        Quiz[] memory endedQuizzes = new Quiz[](count);
        uint index = 0;
        for (uint i = 0; i < quizzes.length; i++) {
            if (quizzes[i].isEnded && quizParticipants[i][participant].attempts > 0) {
                endedQuizzes[index] = quizzes[i];
                index++;
            }
        }
        return endedQuizzes;
    }
}
